import React, { useState, useContext } from "react";
import { Form, Button, Image } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

import { useUserActions } from "../../hooks/user.actions";
import { Context } from "../Layout";

function UpdateProfileForm(props) {
  const { profile } = props;

  const navigate = useNavigate();

  const [validated, setValidated] = useState(false);
  const [form, setForm] = useState(profile);
  const [error, setError] = useState(null);
  const userActions = useUserActions();

  const [avatar, setAvatar] = useState();

  const { setToaster } = useContext(Context);

  const handleSubmit = (event) => {
    event.preventDefault();
    const updateProfileForm = event.currentTarget;

    if (updateProfileForm.checkValidity() === false) {
      event.stopPropagation();
    }

    setValidated(true);

    const data = {
      first_name: form.first_name,
      last_name: form.last_name,
      bio: form.bio,
    };

    const formData = new FormData();

    // Checking for null values in the form and removing it.

    Object.keys(data).forEach((key) => {
      if (data[key]) {
        formData.append(key, data[key]);
      }
    });

    if (avatar) {
      formData.append("avatar", avatar);
    }

    userActions
      .edit(formData, profile.id)
      .then(() => {
        setToaster({
          type: "success",
          message: "–ê–∫–∫–∞—É–Ω—Ç —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω! üöÄ",
          show: true,
          title: "–ê–∫–∫–∞—É–Ω—Ç –æ–±–Ω–æ–≤–ª–µ–Ω!",
        });
        navigate(-1);
      })
      .catch((err) => {
        if (err.message) {
          setError(err.request.response);
        }
      });
  };
  return (
    <Form
      id="registration-form"
      className="border p-4 rounded"
      noValidate
      validated={validated}
      onSubmit={handleSubmit}
    >
      <Form.Group className="mb-3 d-flex flex-column">
        <Form.Label className="text-center">–§–æ—Ç–æ</Form.Label>
        <Image
          src={form.avatar}
          roundedCircle
          width={120}
          height={120}
          className="m-2 border border-primary border-2 align-self-center"
        />
        <Form.Control
          onChange={(e) => setAvatar(e.target.files[0])}
          className="w-50 align-self-center"
          type="file"
          size="sm"
        />
        <Form.Control.Feedback type="invalid">
          –û–ø–ø–∞! –û—à–∏–±–æ—á–∫–∞ –≤—ã—à–ª–∞.
        </Form.Control.Feedback>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>–ò–º—è</Form.Label>
        <Form.Control
          value={form.first_name}
          onChange={(e) => setForm({ ...form, first_name: e.target.value })}
          required
          type="text"
          placeholder="Enter first name"
        />
        <Form.Control.Feedback type="invalid">
          –û–ø–ø–∞!–û—à–∏–±–æ—á–∫–∞ –≤—ã—à–ª–∞.
        </Form.Control.Feedback>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>–§–∞–º–∏–ª–∏—è</Form.Label>
        <Form.Control
          value={form.last_name}
          onChange={(e) => setForm({ ...form, last_name: e.target.value })}
          required
          type="text"
          placeholder="–í–≤–≤–µ–¥–∏—Ç–µ —Ñ–∞–º–∏–ª–∏—é"
        />
        <Form.Control.Feedback type="invalid">
          –û–ø–ø–∞! .
        </Form.Control.Feedback>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Bio</Form.Label>
        <Form.Control
          value={form.bio}
          onChange={(e) => setForm({ ...form, bio: e.target.value })}
          as="textarea"
          rows={3}
          placeholder="A simple bio ... (Optional)"
        />
      </Form.Group>

      <div className="text-content text-danger">{error && <p>{error}</p>}</div>

      <Button variant="primary" type="submit">
        Save changes
      </Button>
    </Form>
  );
}

export default UpdateProfileForm;
