import React, { Component } from 'react';
import axios from 'axios';

class App extends Component {
  state = {
    todos: []
  };
  componentDidMount() {
    this.getTodo();
  }
  getTodo() {
    axios
        .get('http://127.0.0.1:8001/api/')
        .then(req => {
          this.setState({todos: res.data});
        })
        .catch(err => {
